(5, 'Quantity Fied - Shop Pages', '', '/* add a quantity field to shop pages */\n\nfunction shop_page_add_quantity_field() {\n    /** @var WC_Product $product */\n    $product = wc_get_product( get_the_ID() );\n    if ( ! $product->is_sold_individually() && \'variable\' != $product->get_type() && $product->is_purchasable() && $product->is_in_stock() ) {\n        // Obtenemos el ID del producto\n        $product_id = $product->get_id();\n		echo \'<script>var productId = \' . json_encode($product_id) . \';</script>\';\n        // Obtenemos la cantidad del producto en el carrito\n        $cart_item_quantity = 0;\n        foreach ( WC()->cart->get_cart() as $cart_item_key => $cart_item ) {\n            if ( $cart_item[\'product_id\'] == $product_id ) {\n                $cart_item_quantity += $cart_item[\'quantity\'];\n            }\n        }\n\n        // Mostramos el campo de cantidad con la cantidad del carrito\n        woocommerce_quantity_input( array(\n            \'input_value\' => $cart_item_quantity, // Establecemos el valor del campo como la cantidad del carrito\n            \'min_value\'   => 0,\n            \'max_value\'   => $product->backorders_allowed() ? \'\' : $product->get_stock_quantity()\n        ) );\n    }\n	\n}\n\nadd_action( \'woocommerce_after_shop_loop_item\', \'shop_page_add_quantity_field\', 1 );\n\n\nfunction shop_page_quantity_add_to_cart_handler() {\n	wc_enqueue_js( \'\n		$(\".woocommerce .products\").on(\"click\", \".quantity input\", function() {\n			return false;\n		});\n		$(\".woocommerce .products\").on(\"change input\", \".quantity .qty\", function() {\n			var add_to_cart_button = $(this).parents( \".product\" ).find(\".add_to_cart_button\");\n			// For AJAX add-to-cart actions\n			add_to_cart_button.data(\"quantity\", $(this).val());\n			// For non-AJAX add-to-cart actions\n			add_to_cart_button.attr(\"href\", \"?add-to-cart=\" + add_to_cart_button.attr(\"data-product_id\") + \"&quantity=\" + $(this).val());\n		});\n		// Trigger on Enter press\n		$(\".woocommerce .products\").on(\"keypress\", \".quantity .qty\", function(e) {\n			if ((e.which||e.keyCode) === 13) {\n				$( this ).parents(\".product\").find(\".add_to_cart_button\").trigger(\"click\");\n			}\n		});\n	\' );\n}\nadd_action( \'init\', \'shop_page_quantity_add_to_cart_handler\' );\n\nfunction urna_child_tbay_product_layout_style($type) {\n    return \'v15\';\n}\nadd_filter( \'urna_woo_config_product_layout\', \'urna_child_tbay_product_layout_style\', 100, 1 );\n', '', 'global', 10, 0, '2024-04-11 11:13:33', 7, NULL);
